class Block():
    def __init__(self,num):
        self.num=num
        #print(type(self),"block number",num)        
        
    @classmethod
    def new(cls):
        global block
        if block>=2022:
            return None
        if block%5==0:
            block += 1
            return Shape0(block)
        elif block%5==1:
            block += 1
            return Shape1(block)
        elif block%5==2:
            block += 1
            return Shape2(block)
        elif block%5==3:
            block += 1
            return Shape3(block)
        elif block%5==4:
            block += 1
            return Shape4(block)
                    
class Shape0(Block):            # -
    def fall(self,cmds):
        pass
class Shape1(Block):            # + 
    def fall(self,cmds):
        pass
class Shape2(Block):            # _|
    def fall(self,cmds):
        pass
class Shape3(Block):            # |
    def fall(self,cmds):
        pass
class Shape4(Block):            # []
    def fall(self,cmds):
        pass
    
block = 0
coords = [1 if x==">" else -1 for x in ex]
cmds = list(coords)
c = Block.new()
while c is not None:
    c.fall(cmds)
    c = Block.new()
